class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        n=len(points)
        a=[[]for _ in range(2)]
        for i,[x,y] in enumerate(points):
            a[0].append([x+y,i])
            a[1].append([x-y,i])
        for i in range(2):
            a[i].sort()

        # print(a)
        [a00,i00]=a[0][0]
        [a01,i01]=a[0][-1]
        [a10,i10]=a[1][0]
        [a11,i11]=a[1][-1]
        def f(k):
            xx=0
            i,j=0,n-1
            if a[0][i][1]==k:
                i+=1
            if a[0][j][1]==k:
                j-=1
            xx=max(xx,a[0][j][0]-a[0][i][0])
            i,j=0,n-1
            if a[1][i][1]==k:
                i+=1
            if a[1][j][1]==k:
                j-=1
            xx=max(xx,a[1][j][0]-a[1][i][0])
            return xx
        return min(f(i) for i in [i00,i01,i10,i11])
