class Solution {
    public int minimumDistance(int[][] points) {
        int n = points.length;

        int minSum = points[0][0] + points[0][1], maxSum = minSum;
        int minDiff = points[0][0] - points[0][1], maxDiff = minDiff;
        List<Integer> indicesMinSum = new ArrayList<>(), indicesMaxSum = new ArrayList<>();
        List<Integer> indicesMinDiff = new ArrayList<>(), indicesMaxDiff = new ArrayList<>();
        indicesMinSum.add(0); indicesMaxSum.add(0); indicesMinDiff.add(0); indicesMaxDiff.add(0);

        for (int i = 1; i < n; i++) {
            int sum = points[i][0] + points[i][1];
            int diff = points[i][0] - points[i][1];

            if (sum < minSum) {
                minSum = sum;
                indicesMinSum.clear();
                indicesMinSum.add(i);
            } else if (sum == minSum) {
                indicesMinSum.add(i);
            }

            if (sum > maxSum) {
                maxSum = sum;
                indicesMaxSum.clear();
                indicesMaxSum.add(i);
            } else if (sum == maxSum) {
                indicesMaxSum.add(i);
            }

            if (diff < minDiff) {
                minDiff = diff;
                indicesMinDiff.clear();
                indicesMinDiff.add(i);
            } else if (diff == minDiff) {
                indicesMinDiff.add(i);
            }

            if (diff > maxDiff) {
                maxDiff = diff;
                indicesMaxDiff.clear();
                indicesMaxDiff.add(i);
            } else if (diff == maxDiff) {
                indicesMaxDiff.add(i);
            }
        }

        Set<Integer> uniqueIndices = new HashSet<>();
        uniqueIndices.addAll(indicesMinSum);
        uniqueIndices.addAll(indicesMaxSum);
        uniqueIndices.addAll(indicesMinDiff);
        uniqueIndices.addAll(indicesMaxDiff);

        int minMaxDistanceAfterRemoval = Integer.MAX_VALUE;

        for (Integer index : uniqueIndices) {
            int[][] newPoints = new int[n-1][2];
            for (int i = 0, j = 0; i < n; i++) {
                if (i != index) {
                    newPoints[j++] = points[i];
                }
            }

            int newMaxDistance = help(newPoints);

            minMaxDistanceAfterRemoval = Math.min(minMaxDistanceAfterRemoval, newMaxDistance);
        }

        return minMaxDistanceAfterRemoval;
    }

    private int help(int[][] points) {
        int minimumSum = Integer.MAX_VALUE, maximumSum = Integer.MIN_VALUE;
        int minimumDiff = Integer.MAX_VALUE, maximumDiff = Integer.MIN_VALUE;

        for (int[] point : points) {
            int x = point[0], y = point[1];
            int sum = x + y, diff = x - y;

            minimumSum = Math.min(minimumSum, sum);
            maximumSum = Math.max(maximumSum, sum);
            minimumDiff = Math.min(minimumDiff, diff);
            maximumDiff = Math.max(maximumDiff, diff);
        }

        return Math.max(maximumSum - minimumSum, maximumDiff - minimumDiff);
    }
}
