class Solution {
public:
    int findMax(vector<int> v, vector<int> v1, int j){

        int mx1=INT_MIN,mx2=INT_MIN,mn1=INT_MAX,mn2=INT_MAX;
        for(int i=0;i<v.size();i++){
            if(j!=i){
                mx1=max(v[i],mx1);
                mx2=max(v1[i],mx2);
                mn1=min(v[i],mn1);
                mn2=min(v1[i],mn2);
            }
        }
        return max(mx1-mn1, mx2-mn2);
    }
    int minimumDistance(vector<vector<int>>& nums) {
        int n = nums.size();
        vector<int> v(n), v1(n);
        for (int i=0;i<n;i++) {
            v[i] = nums[i][0]+nums[i][1];
            v1[i] = nums[i][0]-nums[i][1];
        }
        long long mx1=0,mx2=0,cx1=1,cx2=1,mn1=0,mn2=0,cn1=1,cn2=1;
        for(int i=1;i<n;i++){
            if(v[mx1]<v[i]){
                cx1=1;
                mx1=i;
            }
            // else if(v[mx1]==v[i]){
            //     cx1++;
            // }

            if(v1[mx2]<v1[i]){
                cx2=1;
                mx2=i;
            }
            // else if(v1[mx1]==v1[i]){
            //     cx2++;
            // }

            if(v[mn1]>v[i]){
                cn1=1;
                mn1=i;
            }
            // else if(v[mn1]==v[i]){
            //     cn1++;
            // }

            if(v1[mn2]>v1[i]){
                cn2=1;
                mn2=i;
            }
            // else if(v1[mn2]==v1[i]){
            //     cn2++;
            // }
        }
        // cout<<mx1<<" "<<mx2<<" "<<mn1<<" "<<mn2<<endl;
        int ans1 = findMax(v,v1,mx1);
        int ans2 = findMax(v,v1,mx2);
        int ans3 = findMax(v,v1,mn1);
        int ans4 = findMax(v,v1,mn2);
        return min({ans1,ans2,ans3,ans4});
    }
};
