class Solution {
public:
    int MaxDist(vector<vector<int> >& A, int N)
    {
        vector<pair<int,int>> V(N), V1(N);

        for (int i = 0; i < N; i++) {
            V[i].first = A[i][0] + A[i][1];
            V1[i].first = A[i][0]- A[i][1];
            V[i].second = i;
            V1[i].second = i;
        }


        sort(V.begin(), V.end());
        sort(V1.begin(), V1.end());
        int ans = 1e9;
        for(int ban = 0 ; ban < N ; ++ban)
        {
            int vb = V[N-1].first,vf=V[0].first,v1b=V1[N-1].first,v1f=V1[0].first;

            if(V.back().second == ban)
                vb = V[N-2].first;
            if(V[0].second == ban)
                vf = V[1].first;
            if(V1.back().second == ban)
                v1b = V1[N-2].first;
            if(V1[0].second == ban)
                v1f = V1[1].first;

            ans = min(ans,max(vb-vf,v1b-v1f));
        }
        return ans;


    }
    int minimumDistance(vector<vector<int>>& points) {


        return MaxDist(points,points.size());
    }
};
