class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        vector<pair<int,int>> a1(n), a2(n);

        // function<pair<int,pair<int,>()> ff = [&](){
        //  return    
        // }
        if(n==2) {
            return abs(points[1][1]-points[0][1]) + abs(points[1][0]-points[0][0]);
        }

        for(int i=0; i<n; ++i) {
            a1[i] = {points[i][0]+points[i][1], i};
            a2[i] = {points[i][0]-points[i][1], i};
        }
        sort(a1.begin(), a1.end());
        sort(a2.begin(), a2.end());
        int mn = a1[n-1].first - a1[0].first;
        pair<int,int> mni = {a1[0].second, a1[n-1].second};
        // int ans = min(a1[n-1].first-a1[1].first, )
        if(a2[n-1].first - a2[0].first > mn) {
            mni = {a2[0].second, a2[n-1].second};
        }

        int curi=mni.first;
        int ans=0;
        // if(a1[0].second==curi) {
        //     ans = max(ans, a1[n-1].first-a1[1].first);
        // }
        // if(a1[n-1].second==curi) {
        //     ans = max(ans, a1[n-2].first - a1[0].first);
        // }
        // if(a2[0].second==curi) {
        //     ans = max(ans, a2[n-1].first-a2[1].first);
        // }
        // if(a2[n-1].second==curi) {
        //     ans = max(ans, a2[n-2].first - a2[0].first);
        // }

        if(a1[0].second==curi) {
            ans = max(ans, a1[n-1].first-a1[1].first);
        }
        else if(a1[n-1].second==curi) {
            ans = max(ans, a1[n-2].first - a1[0].first);
        } else {
            ans = max(ans, a1[n-1].first-a1[0].first);
        }
        if(a2[0].second==curi) {
            ans = max(ans, a2[n-1].first-a2[1].first);
        }
        else if(a2[n-1].second==curi) {
            ans = max(ans, a2[n-2].first - a2[0].first);
        }else {
            ans = max(ans, a2[n-1].first-a2[0].first);
        }

        // cout<<curi<<" "<<ans<<endl;
        curi = mni.second;
        int ans2= ans;

        ans = 0; 
        // for(auto x: a1) cout<<x.second<<" ";cout<<endl;
        // for(auto x: a2) cout<<x.second<<" ";cout<<endl;
        if(a1[0].second==curi) {
            ans = max(ans, a1[n-1].first-a1[1].first);
        }
        else if(a1[n-1].second==curi) {
            ans = max(ans, a1[n-2].first - a1[0].first);
        } else {
            ans = max(ans, a1[n-1].first-a1[0].first);
        }
        if(a2[0].second==curi) {
            ans = max(ans, a2[n-1].first-a2[1].first);
        }
        else if(a2[n-1].second==curi) {
            ans = max(ans, a2[n-2].first - a2[0].first);
        }else {
            ans = max(ans, a2[n-1].first-a2[0].first);
        }
        ans = min(ans, ans2);

        // cout<<curi<<" "<<ans<<endl;


        return ans;


    }
};
