class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size(),i=0,ans=INT_MAX;
        vector<pair<int,int>>g,h;
        for(auto x:points)
        {
            g.push_back({x[0]+x[1],i});
            h.push_back({x[0]-x[1],i});
            i++;
        }
        sort(g.begin(),g.end());
        sort(h.begin(),h.end());
        for(int i=0;i<n;i++)
        {
            int a=g[0].first,b=g[n-1].first;
            int x=h[0].first,y=h[n-1].first;
            if(g[0].second==i)
                a=g[1].first;
            if(g[n-1].second==i)
                b=g[n-2].first;
            if(h[0].second==i)
                x=h[1].first;
            if(h[n-1].second==i)
                y=h[n-2].first;  

            ans=min(ans,max(b-a,y-x));
        }
        return ans;
    }
};
