class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int minsum, maxsum, mindiff, maxdiff;
        int minsum2, maxsum2, mindiff2, maxdiff2;

        minsum = minsum2 = INT_MAX;
        maxsum = maxsum2 = INT_MIN;
        mindiff = mindiff2 = INT_MAX;
        maxdiff = maxdiff2 = INT_MIN;
        vector<int> vsum;
        vector<int> vdiff;
        int ans = INT_MAX;

        for(int i = 0; i < points.size(); ++i){
            int sum = points[i][0] + points[i][1];
            int diff = points[i][0] - points[i][1];

            // vsum.push_back(sum);
            // vdiff.push_back(diff);

            if(sum > maxsum){
                maxsum2 = maxsum;
                maxsum = sum;
            }else if(sum > maxsum2){
                maxsum2 = sum;
            }

            if(sum < minsum){
                minsum2 = minsum;
                minsum = sum;
            }else if(sum < minsum2)
                minsum2 = sum;

            if(diff > maxdiff){
                maxdiff2 = maxdiff;
                maxdiff = diff;
            }else if(diff > maxdiff2){
                maxdiff2 = diff;
            }

            if(diff < mindiff){
                mindiff2 = mindiff;
                mindiff = diff;
            }else if(diff < mindiff2){
                mindiff2 = diff;
            }
        }

        // sort(vsum.begin(), vsum.end());
        // sort(vdiff.begin(), vdiff.end());


        // printf("%d %d %d %d\n",maxsum, maxsum2, minsum2, minsum);
        // printf("%d %d %d %d\n",maxdiff, maxdiff2, mindiff2, mindiff);
        int n = points.size();

        for(int i = 0; i < points.size(); ++i){
            int sum = points[i][0] + points[i][1];
            int diff = points[i][0] - points[i][1];

            int mxsum = sum == maxsum ? maxsum2 : maxsum;
            int mnsum = sum == minsum ? minsum2 : minsum;

            int mxdiff = diff == maxdiff ? maxdiff2 : maxdiff;
            int mndiff = diff == mindiff ? mindiff2 : mindiff;

            int mx = max(mxsum - mnsum, mxdiff - mndiff);
            // cout<<mx<<endl;
            ans = min(ans, mx);
        }


        return ans;


