bool cmp(const pair<int, int> &a, const pair<int, int> &b)
{
    return a.second < b.second;
}
class Solution {
public:

    int getDist(vector<pair<int, int>> &v)
    {
        sort(v.begin(), v.end());
        int ans = v[v.size()-1].first - v[0].first;
        sort(v.begin(), v.end(), cmp);
        ans = max(ans, v[v.size()-1].second - v[0].second);
        return ans;
    }

    int minimumDistance(vector<vector<int>>& points) {
        vector<pair<int, int>> v;
        vector<pair<int, int>> v2;
        for(int i = 0; i < points.size(); i++)
        {
            v.push_back({points[i][0]+points[i][1], points[i][1]-points[i][0]});
        }
        for(pair<int, int> p : v)
        {
            v2.push_back({p.second, p.first});
        }
        int ans = 2e9;
        sort(v.begin(), v.end());
        pair<int, int> bruh = v.back();
        v.pop_back();
        ans = min(ans, getDist(v));
        v.push_back(bruh);
        sort(v.begin(), v.end(), greater<pair<int, int>>());
        v.pop_back();
        ans = min(ans, getDist(v));

        sort(v2.begin(), v2.end());
        bruh = v2.back();
        v2.pop_back();
        ans = min(ans, getDist(v2));
        v2.push_back(bruh);
        sort(v2.begin(), v2.end(), greater<pair<int, int>>());
        v2.pop_back();
        ans = min(ans, getDist(v2));

        return ans;
    }
};
