class Solution {
public:
    int minimumDistance(vector<vector<int>>& a) {
         int n=a.size();
        sort(a.begin(),a.end());
       vector<int>pre(n,0);
        vector<int>suff(n,0);
        vector<int>prep(n,0);
        vector<int>suffp(n,0);
        vector<int>pren(n,0);
        vector<int>suffn(n,0);
        pre[0]=0;

        int map=a[0][0]+a[0][1];
        int man=a[0][0]-a[0][1];
         prep[0]=map;
        pren[0]=man;

        for(int i=1;i<a.size();i++)
        {
            pre[i]=pre[i-1];
            int cap=a[i][0]+a[i][1];
            int can=a[i][0]-a[i][1];
            pre[i]=max({pre[i],cap-map,can-man});
            map=min(map,cap);
            man=min(can,man);
            prep[i]=map;
            pren[i]=man;

        }

        map=a[n-1][0]+a[n-1][1];
        man=a[n-1][0]-a[n-1][1];
        suff[n-1]=0;
        suffp[n-1]=map;
        suffn[n-1]=man;
        for(int i=n-2;i>=0;i--)
        {
            suff[i]=suff[i+1];
            int cap=a[i][0]+a[i][1];
            int can=a[i][0]-a[i][1];
            suff[i]=max({suff[i],map-cap,man-can});
            map=max(map,cap);
            man=max(can,man);
             suffp[i]=map;
        suffn[i]=man;

        }
        int ans=min(pre[n-2],suff[1]);
        for(int i=1;i<a.size()-1;i++)
        {
            ans=min(ans,max({pre[i-1],suff[i+1],suffp[i+1]-prep[i-1],suffn[i+1]-pren[i-1]}));


        }
        return ans;



    }
};
