#define ff first
#define ss second
class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        set<int> a,b;
        map<int,int> m1,m2;
        vector<pair<int,int>> v;
        for(auto x:points){
            v.push_back({x[0],x[1]});
        }
        int n=v.size();
        for(int i=0; i<n; i++){

            int x=v[i].ff+v[i].ss;
            int y=v[i].ff-v[i].ss;
            a.insert(x);
            b.insert(y);
            m1[x]++;
            m2[y]++;

        }
        int ans=1e9;
        for(int i=0; i<n; i++){
            int x=v[i].ff+v[i].ss;
            int y=v[i].ff-v[i].ss;

            if(m1[x]==1){
                auto it=a.find(x);
                a.erase(it);
            }
            if(m2[y]==1){
                auto it=b.find(y);
                b.erase(it);
            }
            auto l1=a.begin();
            auto r1=b.begin();
            auto l2=a.end();
            l2--;
            auto r2=b.end();
            r2--;
            ans=min(ans, max(*l2-*l1, *r2-*r1));
            a.insert(x);
            b.insert(y);
        }
        return ans;
    }
};
