class Solution {
public:
    int minimumDistance(vector<vector<int>> v) {
        multiset<pair<int,int>>diff,sum;
        int n=v.size(),ans=1e9;
        for(int i=0;i<n;i++)
        {
            diff.insert({v[i][0]-v[i][1],v[i][0]+v[i][1]});
            sum.insert({v[i][0]+v[i][1],v[i][0]-v[i][1]});
        }
        auto it=*diff.begin();
        diff.erase(diff.begin());
        swap(it.first,it.second);
        sum.erase(sum.find(it));
        ans=min(ans,max(diff.rbegin()->first-diff.begin()->first,sum.rbegin()->first-sum.begin()->first));
       sum.insert(it);
       swap(it.first,it.second);
        diff.insert(it);


        it=*diff.rbegin();
        diff.erase(--diff.end());
        swap(it.first,it.second);
        sum.erase(sum.find(it));
        ans=min(ans,max(diff.rbegin()->first-diff.begin()->first,sum.rbegin()->first-sum.begin()->first));
        sum.insert(it);
        swap(it.first,it.second);
        diff.insert(it);



        it=*sum.begin();
        sum.erase(sum.begin());
        swap(it.first,it.second);
        diff.erase(diff.find(it));
        ans=min(ans,max(diff.rbegin()->first-diff.begin()->first,sum.rbegin()->first-sum.begin()->first));
        diff.insert(it);
        swap(it.first,it.second);
        sum.insert(it);


        it=*sum.rbegin();
        sum.erase(--sum.end());
        swap(it.first,it.second);
        diff.erase(diff.find(it));
        ans=min(ans,max(diff.rbegin()->first-diff.begin()->first,sum.rbegin()->first-sum.begin()->first));
        diff.insert(it);
        swap(it.first,it.second);
        sum.insert(it);



        return ans;
    }
};
