class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        long long ans=1e9,n=points.size();
        vector<pair<int,int>> v;
        for(int i=0;i<n;i++){
            v.push_back({points[i][0],points[i][1]});
        }
        multiset<long long> s1,s2;
        for(int i=0;i<n;i++){
            s1.insert(v[i].first+v[i].second);
            s2.insert(v[i].first-v[i].second);
        }
        for(int i=0;i<n;i++){
            s1.erase(s1.find(v[i].first+v[i].second));
            s2.erase(s2.find(v[i].first-v[i].second));
            long long mx=(*(--s1.end())- *s1.begin());
            mx=max(mx,*(--s2.end())- *s2.begin());
            s1.insert(v[i].first+v[i].second);
            s2.insert(v[i].first-v[i].second);
            ans=min(ans,mx);
        }
        return ans;
    }
};
