class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        vector<pair<int,int>> p[4];
        for(int i=0;i<n;i++)
        {
            int x=points[i][0],y=points[i][1];
            p[0].push_back({x+y,i});
            p[1].push_back({-x+y,i});
            p[2].push_back({x-y,i});
            p[3].push_back({-x-y,i});
        }
	    for(int i=0;i<4;i++)sort(p[i].begin(),p[i].end());
	    int mx=0;
        map<int,int,greater<int>> m;
	    for(int i=0;i<4;i++)
	    {
            if(mx<p[i][n-1].first-p[i][0].first){
                m.clear();
                mx=p[i][n-1].first-p[i][0].first;
                m[p[i][n-1].second]++;
                m[p[i][0].second]++;
            }else if(mx==p[i][n-1].first-p[i][0].first){
                m[p[i][n-1].second]++;
                m[p[i][0].second]++;
            }
        }
        auto f=[](vector<vector<int>> points,int pos){
            points.erase(points.begin()+pos);
            // for(auto x:points)cout<<x[0]<<" "<<x[1]<<endl;
            int n=points.size();
            vector<pair<int,int>> p[4];
            for(int i=0;i<n;i++)
            {
                int x=points[i][0],y=points[i][1];
                p[0].push_back({x+y,i});
                p[1].push_back({-x+y,i});
                p[2].push_back({x-y,i});
                p[3].push_back({-x-y,i});
            }
            for(int i=0;i<4;i++)sort(p[i].begin(),p[i].end());
            int mx=0;
            map<int,int,greater<int>> m;
            for(int i=0;i<4;i++)
            {
                if(mx<p[i][n-1].first-p[i][0].first){
                    m.clear();
                    mx=p[i][n-1].first-p[i][0].first;
                    m[p[i][n-1].second]++;
                    m[p[i][0].second]++;
                }else if(mx==p[i][n-1].first-p[i][0].first){
                    m[p[i][n-1].second]++;
                    m[p[i][0].second]++;
                }
            }
            return mx;
        };
        int ans=INT_MAX;
        for(auto x:m){
            // cout<<x.first<<" "<<x.second<<endl;
            ans=min(ans,f(points,x.first));
            // if(x.second>2)break;
        }
        return ans;
    }
};
