class Solution {

public:
    int minimumDistance(vector<vector<int>>& p) {
        int n = p.size();
        long long sumx = 0;
        long long sumy = 0;

        vector<int> x(n+1);
        vector<int> y(n+1);
        vector<int> premin(n+2);
        vector<int> premax(n+2);
        vector<int> sufmin(n+2);
        vector<int> sufmax(n+2);

        sort(p.begin(), p.end());
        for(int i = 1; i<=n;++i) {
            x[i] = p[i-1][0];
            y[i] = p[i-1][1];
            sumx+=x[i];
            sumy+=x[i];
        }
        vector<int> gao;
        priority_queue<pair<int,int> > PQ;

        for(int i = 1; i<=n;++i) {
            if (x[i] >= sumx / n && y[i] >= sumy / n) {
                int len = abs(x[i]-sumx/n) + abs(y[i]-sumy/n);
                PQ.push({-len, i});
                if(PQ.size()>50) {
                    PQ.pop();
                }
            }
        }
        while(!PQ.empty()){
            gao.push_back(PQ.top().second);
            PQ.pop();
        }

        for(int i = 1; i<=n;++i) {
            if (x[i] >= sumx / n && y[i] <= sumy / n) {
                int len = abs(x[i]-sumx/n) + abs(y[i]-sumy/n);
                PQ.push({-len, i});
                if(PQ.size()>50) {
                    PQ.pop();
                }
            }
        }
        while(!PQ.empty()){
            gao.push_back(PQ.top().second);
            PQ.pop();
        }

        for(int i = 1; i<=n;++i) {
            if (x[i] <= sumx / n && y[i] >= sumy / n) {
                int len = abs(x[i]-sumx/n) + abs(y[i]-sumy/n);
                PQ.push({-len, i});
                if(PQ.size()>50) {
                    PQ.pop();
                }
            }
        }

        while(!PQ.empty()){
            gao.push_back(PQ.top().second);
            PQ.pop();
        }


        for(int i = 1; i<=n;++i) {
            if (x[i] <= sumx / n && y[i] <= sumy / n) {
                int len = abs(x[i]-sumx/n) + abs(y[i]-sumy/n);
                PQ.push({-len, i});
                if(PQ.size()>50) {
                    PQ.pop();
                }
            }
        }

        while(!PQ.empty()){
            gao.push_back(PQ.top().second);
            PQ.pop();
        }


        int ans = 2e9;
        for(int i:gao) {
            int tmp = 0;
            for(int j:gao) {
                if(j==i) {
                    continue;
                }
                for(int k:gao) {
                    if(k==i) {
                        continue;
                    }
                    if(k==j) {
                        continue;
                    }
                    int len = abs(x[j] - x[k]) + abs(y[j]-y[k]);
                    tmp = max(tmp, len);
                }
            }
            ans = min(ans, tmp);
        }

        return ans;
    }
};
