class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        set<pair<int, int>> sum, diff;
        for(int i=0;i<points.size();i++){
            sum.insert({points[i][0]+points[i][1], i});
            diff.insert({points[i][0]-points[i][1], i});
        }

        int ans=INT_MAX;
        for(int i=0;i<points.size();i++){
            sum.erase({points[i][0]+points[i][1], i});
            diff.erase({points[i][0]-points[i][1], i});

            int temp = max(sum.rbegin()->first - sum.begin()->first, diff.rbegin()->first - diff.begin()->first);
            ans=min(ans, temp);

            sum.insert({points[i][0]+points[i][1], i});
            diff.insert({points[i][0]-points[i][1], i});

        }

        return ans;
    }
};
