class Solution {
public:
    //pair<int,int> p[4][100010];
    int minimumDistance(vector<vector<int>>& points) {
        pair<int,int> p[4][100010];
        int n=points.size();
        int ans=1e9,mx=0;
        for(int i=0;i<n;i++){
            int x=points[i][0],y=points[i][1];
            p[0][i]={x+y,i};
  		    p[1][i]={-x+y,i};
  		    p[2][i]={x-y,i};
  		    p[3][i]={-x-y,i};
        }
        for(int i=0;i<4;i++) sort(p[i],p[i]+n);
        for(int i=0;i<4;i++){
            mx=max(p[i][n-1].first-p[i][0].first,mx);
        }
        vector<int> q;
        for(int i=0;i<4;i++){
            if(p[i][n-1].first-p[i][0].first==mx){
                q.push_back(p[i][n-1].second);
                q.push_back(p[i][0].second);
            } 
        }
        for(int i=0;i<q.size();i++){
            int id=q[i];
            mx=0;
            for(int j=0;j<4;j++){
                int l=0,r=n-1;
                if(p[j][l].second==id) l++;
                if(p[j][r].second==id) r--;
                mx=max(mx,p[j][r].first-p[j][l].first);
            }
            ans=min(ans,mx);
        }
        return ans;
    }
};
