class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        vector<vector<int>> v;
        for(auto point:points){
            int x=point[0],y=point[1];
            v.push_back({x+y,x-y});
        }
        sort(v.begin(),v.end());

        vector<int> suffixMin(n,INT_MAX),suffixMax(n,INT_MIN);
        suffixMin[n-1]=v[n-1][1];
        suffixMax[n-1]=v[n-1][1];
        for(int i=n-2;i>=0;i--){
            suffixMin[i]=min(suffixMin[i+1],v[i][1]);
            suffixMax[i]=max(suffixMax[i+1],v[i][1]);
        }
        int result=max(abs(v[1][0]-v[n-1][0]),abs(suffixMax[1]-suffixMin[1]));
        int pyMin=v[0][1],pyMax=v[0][1];
        for(int i=1;i<n-1;i++){
            int x=v[i][0],y=v[i][1];
            int curMin=min(pyMin,suffixMin[i+1]);
            int curMax=max(pyMax,suffixMax[i+1]);
            result=min(result,max(abs(v[0][0]-v[n-1][0]),abs(curMax-curMin)));
            pyMin=min(y,pyMin);
            pyMax=max(y,pyMax);
        }

        result=min(result,max(abs(v[0][0]-v[n-2][0]),abs(pyMax-pyMin)));

        return result;

    }
};
