class Solution {
public:
    int helper(vector<vector<int>>& points) {
        int n = points.size(), minsum, maxsum, mindiff, maxdiff;
        minsum = maxsum = points[0][0] + points[0][1];
        mindiff = maxdiff = points[0][0] - points[0][1];
        for (int i = 1; i < n; i++) {
            int sum = points[i][0] + points[i][1];
            int diff = points[i][0] - points[i][1];
            if (sum < minsum) {
                minsum = sum;
            } else if (sum > maxsum) {
                maxsum = sum;
            } 

            if (diff < mindiff) {
                mindiff = diff;
            } else if (diff > maxdiff) {
                maxdiff = diff;
            }
        }

        return max(maxsum - minsum, maxdiff - mindiff);
    }

    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
        int minSum = points[0][0] + points[0][1], maxSum = points[0][0] + points[0][1];
        int minDiff = points[0][0] - points[0][1], maxDiff = points[0][0] - points[0][1];
        int a = 0, b = 0, c = 0, d = 0;
        for (int i=0; i<n; i++) {
            int sum = points[i][0] + points[i][1], diff = points[i][0] - points[i][1];
            if (sum < minSum) {
                a = i;
                minSum = sum;
            } else if (sum > maxSum) {
                b = i;
                maxSum = sum;
            } 

            if (diff < minDiff) {
                c = i;
                minDiff = diff;
            } else if (diff > maxDiff) {
                d = i;
                maxDiff = diff;
            }
        }

        vector<vector<int>> v;
        for (int i=0; i<n; i++) {
            if (i == a) {
                continue;
            }
            v.push_back(points[i]);
        }
        int ans = helper(v);
        v.clear();

        for (int i=0; i<n; i++) {
            if (i == b) {
                continue;
            }
            v.push_back(points[i]);
        }
        ans = min(ans, helper(v));
        v.clear();

        for (int i=0; i<n; i++) {
            if (i == c) {
                continue;
            }
            v.push_back(points[i]);
        }
        ans = min(ans, helper(v));
        v.clear();

        for (int i=0; i<n; i++) {
            if (i == d) {
                continue;
            }
            v.push_back(points[i]);
        }
        ans = min(ans, helper(v));
        v.clear();
        return ans;
    }
};
