class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
        vector<int> sum(n), diff(n);
        int s1 = INT_MIN, s2 = INT_MIN, d1 = INT_MIN, d2 = INT_MIN;
        int ms1 = INT_MAX, ms2 = INT_MAX, md1 = INT_MAX, md2 = INT_MAX;

        for(int i=0; i<n; i++){
            sum[i] = points[i][0] + points[i][1];
            diff[i] = points[i][0] - points[i][1];
            if(sum[i] > s1){
                s2 = s1;
                s1 = sum[i];
            }
            else{
                s2 = max(s2, sum[i]);
            }
            if(diff[i] > d1){
                d2 = d1;
                d1 = diff[i];
            }
            else{
                d2 = max(d2, diff[i]);
            }
            if(sum[i] < ms1){
                ms2 = ms1;
                ms1 = sum[i];
            }
            else{
                ms2 = min(ms2, sum[i]);
            }
            if(diff[i] < md1){
                md2 = md1;
                md1 = diff[i];
            }
            else{
                md2 = min(md2, diff[i]);
            }
        }

        int ans = max(s1-ms1, d1-md1);

        for(int i=0; i<n; i++){
            int s = points[i][0] + points[i][1];
            int d = points[i][0] - points[i][1];

            if(s == s1){
                ans = min(ans, max(s2-ms1, d1-md1));
                if(d == d1){
                    ans = min(ans, max(s2-ms1, d2-md1));
                }
                if(d == md1){
                    ans = min(ans, max(s2-ms1, d1-md2));
                }
            }
            if(s == ms1){
                ans = min(ans, max(s1-ms2, d1-md1));
                if(d == d1){
                    ans = min(ans, max(s1-ms2, d2-md1));
                }
                if(d == md1){
                    ans = min(ans, max(s1-ms2, d1-md2));
                }
            }
            if(d == d1){
                ans = min(ans, max(s1-ms1, d2-md1));
                if(s == s1){
                    ans = min(ans, max(s2-ms1, d2-md1));
                }
                if(s == ms1){
                    ans = min(ans, max(s1-ms2, d2-md1));
                }
            }
            if(d == md1){
                ans = min(ans, max(s1-ms1, d1-md2));
                if(s == s1){
                    ans = min(ans, max(s2-ms1, d1-md2));
                }
                if(s == ms1){
                    ans = min(ans, max(s1-ms2, d1-md2));
                }
            }
        }


        // sort(sum.begin(), sum.end());
        // sort(diff.begin(), diff.end());

        // int s1 = sum[n-1]-sum[1];
        // int s2 = sum[n-2]-sum[0];
        // int d1 = diff[n-1]-diff[1];
        // int d2 = diff[n-2]-diff[0];

//         int a = max({d1, s1});
//         int b = max({d2, s2});

//         int mx = max(sum[n-2]-sum[0], diff[n-2]-diff[0]);

        // int ans = min(a, b);
        // int ans = min(sum[n-1]-sum[1] + diff[n-1]-diff[1], sum[n-2]-sum[0] + diff[n-2]-diff[0]);

        return ans;
    }
};
