class Solution {
public:

    int solve(vector<vector<int>>& nums){
        vector<pair<int,int>> V(nums.size()), V1(nums.size());
        int n = nums.size();
        for (int i = 0; i < nums.size(); i++) {
            V[i] = {(nums[i][0] + nums[i][1]),i};
            V1[i] = {(nums[i][0] - nums[i][1]),i};
        }

        // Sorting both the vectors
        sort(V.begin(), V.end());
        sort(V1.begin(), V1.end());
        //maximum and minimum
        int maximum = max(V[n-1].first - V[0].first, V1[n-1].first - V1[0].first);
        return maximum;
    }

    int minimumDistance(vector<vector<int>>& nums) {
        vector<pair<int,int>> V(nums.size()), V1(nums.size());
        int n = nums.size();
        for (int i = 0; i < nums.size(); i++) {
            V[i] = {(nums[i][0] + nums[i][1]),i};
            V1[i] = {(nums[i][0] - nums[i][1]),i};
        }

        // Sorting both the vectors
        sort(V.begin(), V.end());
        sort(V1.begin(), V1.end());
        //maximum and minimum
        int maximum = max(V[n-1].first - V[0].first, V1[n-1].first - V1[0].first);
        int temp1 = V[n-1].first - V[0].first;
        int temp2 = V1[n-1].first - V1[0].first;
        int index1 = 0;
        int index2 = 0;
        if(temp1>temp2){
            index1 = V[n-1].second;
            index2 = V[0].second;
        }
        else{
            index1 = V1[n-1].second;
            index2 = V1[0].second;
        }
        // cout<<maximum<<endl;
        // cout << index1<<" "<<index2 << endl;

        vector<vector<int>> points;
        int ans = 0;
        for(int i = 0;i<nums.size();i++){
            if(i != index1){
                points.push_back(nums[i]);
            }
        }

        ans = solve(points);
        vector<vector<int>> points1;
        for(int i = 0;i<nums.size();i++){
            if(i != index2){
                points1.push_back(nums[i]);
            }
        }

        ans = min(solve(points1),ans);
        return ans;
    }
};
