class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        if(points.size() <2) {
            return 0;
        }

        auto index = GetIndex(points, -1);

        int result = INT_MAX;
        for(auto idx : index) {
            result = min(result, GetDist(points, idx));
        }

        return result;
    }

    vector<int> GetIndex(vector<vector<int>>& points, int exclude) {
        int n = points.size();

        int min0_idx = exclude == 0 ? 1 : 0;
        int max0_idx = exclude == 0 ? 1 : 0;
        int min1_idx = exclude == 0 ? 1 : 0;
        int max1_idx = exclude == 0 ? 1 : 0;

        for(int i=0; i<n; i++) {
            if(i == exclude) {
                continue;
            }

            int x = points[i][0];
            int y = points[i][1];

            if(x+y >  points[max0_idx][0] + points[max0_idx][1]) {
                max0_idx = i;
            }

            if(x+y <  points[min0_idx][0] + points[min0_idx][1]) {
                min0_idx = i;
            }

            if(x-y >  points[max1_idx][0] - points[max1_idx][1]) {
                max1_idx = i;
            }

            if(x-y <  points[min1_idx][0] - points[min1_idx][1]) {
                min1_idx = i;
            }
        }

        return {min0_idx, max0_idx, min1_idx, max1_idx};
    }

    int GetDist(vector<vector<int>>& points, int exclude) {
        auto index = GetIndex(points, exclude);
        int dist = INT_MIN;

        int n = points.size();

        int min_sum = points[index[0]][0] + points[index[0]][1];
        int max_sum = points[index[1]][0] + points[index[1]][1];
        int min_range = points[index[2]][0] - points[index[2]][1];
        int max_range = points[index[3]][0] - points[index[3]][1];

        for(int i=0; i<n; i++) {
            if(i == exclude) {
                continue;
            }

            int x = points[i][0];
            int y = points[i][1];

            dist = max(dist, x+y - min_sum);
            dist = max(dist, max_sum - (x+y));
            dist = max(dist, x-y - min_range);
            dist = max(dist, max_range - (x-y));
        }

        return dist;
    }


};
