class Solution {
    typedef long long ll;
    typedef pair<ll,ll> pii;
    typedef vector<pii> vii;
    typedef vector<ll> vi;
    set<ll> st[4];
    map<ll,ll> mp[4];
    int xx[4]={1,-1,1,-1};
    int yy[4]={1,1,-1,-1};
public:
    void add(int op,ll x) {
        st[op].insert(x);
        mp[op][x]++;
    }
    void upd(int op,ll x) {
        mp[op][x]--;
        if(!mp[op][x]) st[op].erase(x);
    }
    ll qry(int op) {
        return abs(*st[op].begin()-*st[op].rbegin());
    }
    int minimumDistance(vector<vector<int>>& p) {
        ll n=p.size();
        for(int i=1;i<=n;i++) {
            ll x=p[i-1][0];
            ll y=p[i-1][1];
            add(0,x+y);
            add(1,-x+y);
            add(2,x-y);
            add(3,-x-y);
        }
        ll res=2e18;
        for(int i=1;i<=n;i++) {
            ll x=p[i-1][0];
            ll y=p[i-1][1];
            ll mx=0;
            for(int j=0;j<4;j++) {
                ll su=xx[j]*x+yy[j]*y;
                upd(j,su);
            }
            for(int j=0;j<4;j++) {
                mx=max(mx,qry(j));
            }
            res=min(res,mx);
            for(int j=0;j<4;j++) {
                ll su=xx[j]*x+yy[j]*y;
                add(j,su);
            }
        }
        return res;
    }
};
