class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        vector<pair<int,int>> sum,diff;
        int n = points.size();
        for(int i=0;i<n;i++){
            sum.push_back({points[i][0]+points[i][1],i});
            diff.push_back({points[i][0]-points[i][1],i});
        }
        sort(sum.begin(),sum.end());
        sort(diff.begin(),diff.end());
        vector<int> v;
        int mn=INT_MAX;

        for(int i=0;i<n;i++){
            int dis1,dis2,dis3,dis4;
            for(int j=0;j<n;j++){
                if(sum[j].second!=i){
                    dis1=sum[j].first;
                    break;
                }
            }
            for(int j=n-1;j>=0;j--){
                if(sum[j].second!=i){
                    dis2=sum[j].first;
                    break;
                }
            }
            for(int j=0;j<n;j++){
                if(diff[j].second!=i){
                    dis3=diff[j].first;
                    break;
                }
            }
            for(int j=n-1;j>=0;j--){
                if(diff[j].second!=i){
                    dis4=diff[j].first;
                    break;
                }
            }
            v.push_back(max(dis2-dis1,dis4-dis3));
        }
        sort(v.begin(),v.end());

        return v[0];

    }
};
