class Solution {
public:

    int minimumDistance(vector<vector<int>>& points) {

        multiset<int> sum,diff;

        for(auto i:points){
            sum.insert(i[0]+i[1]),diff.insert(i[0]-i[1]);
        }

        int ans=INT_MAX;
        for(auto i:points){
            sum.erase(sum.find(i[0]+i[1])),diff.erase(diff.find(i[0]-i[1]));

            auto it1=sum.begin(),it2=sum.end();
            it2--;

            int maxDiff=(*it2)-(*it1);

            auto it3=diff.begin(),it4=diff.end();
            it4--;

            maxDiff=max(maxDiff,(*it4)-(*it3));
            ans=min(ans,maxDiff);
            sum.insert(i[0]+i[1]),diff.insert(i[0]-i[1]);
        }
        return ans;
    }
};
