class Solution {
public:
    int fast_max(vector<vector<int>>& points, int skip) {
        vector<pair<int, int>> plus;
        vector<pair<int, int>> minus;

        for (int i = 0; i < points.size(); ++i) {
            if (i == skip) continue;
            plus.push_back({points[i][0] + points[i][1], i});
            minus.push_back({points[i][0] - points[i][1], i});
        }

        sort(plus.begin(), plus.end());
        sort(minus.begin(), minus.end());

        return max(plus.back().first - plus.front().first, minus.back().first - minus.front().first);
    }

    int minimumDistance(vector<vector<int>>& points) {
        vector<pair<int, int>> plus;
        vector<pair<int, int>> minus;

        for (int i = 0; i < points.size(); ++i) {
            plus.push_back({points[i][0] + points[i][1], i});
            minus.push_back({points[i][0] - points[i][1], i});
        }

        sort(plus.begin(), plus.end());
        sort(minus.begin(), minus.end());

        vector<int> to_del;

        if (plus.back().first - plus.front().first > minus.back().first - minus.front().first) {
            // delete 
            to_del.push_back(plus.back().second);
            to_del.push_back(plus.front().second);
        } else if (plus.back().first - plus.front().first == minus.back().first - minus.front().first) {
            to_del.push_back(plus.back().second);
            to_del.push_back(plus.front().second);
            to_del.push_back(minus.back().second);
            to_del.push_back(minus.front().second);
        } else {
            to_del.push_back(minus.back().second);
            to_del.push_back(minus.front().second);
        }


        int r = -1;
        for (int i = 0; i < to_del.size(); ++i) {
            int v = fast_max(points, to_del[i]);
            if (r == -1 || v < r) r = v;
        }

        return r;   
    }
};
