class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        sort(points.begin(), points.end());
        int mina = 1e9, minb = 1e9, m = 0, ai = -1, bi = -1;
        int p = -1, q = -1;
        for (int i = 0; i < points.size(); i++) {
            int x = points[i][0], y = points[i][1];
            int a = x + y, b = x - y;
            int da = a - mina, db = b - minb;
            if (da > m) {
                m = da;
                p = i;
                q = ai;
            }
            if (db > m) {
                m = db;
                p = i;
                q = bi;
            }
            if (a < mina) {
                mina = a;
                ai = i;
            }
            if (b < minb) {
                minb = b;
                bi = i;
            }
        }
        // cout << p << " " << q << endl;
        return min(f(points, p), f(points, q));

    }

private:
    int f(vector<vector<int>>& points, int j) {
        int mina = 1e9, minb = 1e9, m = 0;
        for (int i = 0; i < points.size(); i++) {
            if (i == j) continue;
            int x = points[i][0], y = points[i][1];
            int a = x + y, b = x - y;
            int da = a - mina, db = b - minb;
            m = max(m, max(da, db));
            if (a < mina) {
                mina = a;
            }
            if (b < minb) {
                minb = b;
            }
        }
        return m;
    }
};
