class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        n = len(points)
        v1, v2 = [], []
        for i in range(n):
            v1.append([points[i][0] + points[i][1], i])
            v2.append([points[i][0] - points[i][1], i])

        v1.sort()
        v2.sort()

        ans = float(inf)
        for i in range(n):
            res = -float(inf)
            if v1[-1][1] == i:
                a = v1[-2][0]
            else:
                a = v1[-1][0]

            if v1[0][1] == i:
                b = v1[1][0]
            else:
                b = v1[0][0]

            res = max(res, a - b)

            if v2[-1][1] == i:
                a = v2[-2][0]
            else:
                a = v2[-1][0]

            if v2[0][1] == i:
                b = v2[1][0]
            else:
                b = v2[0][0]

            res = max(res, a - b)

            ans = min(ans, res)

        return ans
