// https://www.geeksforgeeks.org/maximum-manhattan-distance-between-a-distinct-pair-from-n-coordinates/

class Solution {
public:
    int minimumDistance(vector<vector<int>>& A) {
        int n = A.size();
        multiset<int> x, y;
        for (int i = 0; i < n; i++) {
            x.insert(A[i][0] + A[i][1]);
            y.insert(A[i][0] - A[i][1]);
        }
        int res = INT_MAX;
        for (int i = 0; i < n; i++) {
            int sum = A[i][0] + A[i][1];
            int diff = A[i][0] - A[i][1];
            x.erase(x.find(sum));
            y.erase(y.find(diff));
            res = min(res, max(*x.rbegin() - *x.begin(), *y.rbegin() - *y.begin()));
            x.insert(sum);
            y.insert(diff);
        }
        return res;
    }
};
