class Solution {
public:
    int minDistance(vector<vector<int>>& points, int ind){
        int minsum=INT_MAX, maxsum=INT_MIN, mindiff=INT_MAX, maxdiff=INT_MIN;

        if(ind!=0)
        {
            minsum = maxsum = points[0][0] + points[0][1];
            mindiff = maxdiff = points[0][0] - points[0][1];
        }
        else
        {
            minsum = maxsum = points[1][0] + points[1][1];
            mindiff = maxdiff = points[1][0] - points[1][1];
        }
        for (int i = 0; i < points.size(); i++) {
            if(i==ind)
                continue;
            int sum = points[i][0] + points[i][1];
            int diff = points[i][0] - points[i][1];
            if (sum < minsum)
            {
                minsum = sum;
            }
            else if (sum > maxsum)
            {
                maxsum = sum;
            }
            if (diff < mindiff)
            {
                mindiff = diff;
            }
            else if (diff > maxdiff)
            {
                maxdiff = diff;
            }
        }
        int res = max(maxsum - minsum, maxdiff - mindiff);
        return res;
    }
    int minimumDistance(vector<vector<int>>& points) {

        int minsum=INT_MAX, maxsum=INT_MIN, mindiff=INT_MAX, maxdiff=INT_MIN;
        int xminsum=-1, xmaxsum=-1, xmindiff=-1, xmaxdiff=-1;

        minsum = maxsum = points[0][0] + points[0][1];
        xminsum = xmaxsum = 0;
        mindiff = maxdiff = points[0][0] - points[0][1];
        xmindiff = xmaxdiff = 0;
        for (int i = 1; i < points.size(); i++) {
            int sum = points[i][0] + points[i][1];
            int diff = points[i][0] - points[i][1];
            if (sum < minsum)
            {
                minsum = sum;
                xminsum=i;
            }
            else if (sum > maxsum)
            {
                maxsum = sum;
                xmaxsum=i;
            }
            if (diff < mindiff)
            {
                mindiff = diff;
                xmindiff=i;
            }
            else if (diff > maxdiff)
            {
                maxdiff = diff;
                xmaxdiff=i;
            }
        }
        int res = max(maxsum - minsum, maxdiff - mindiff);
        int r1=minDistance(points,xmaxsum);
        int r2=minDistance(points,xminsum);
        int r3=minDistance(points,xmaxdiff);
        int r4=minDistance(points,xmindiff);
        return min(min(r1,r2),min(r3,r4));
    }
};
