class Solution {
public:
    int cal(vector<vector<int>> &a,int j){
        int n = a.size();
        vector<array<int,2>> add,sub;
        for(int i=0;i<n;i++){
            if(i==j) continue;
            add.push_back({a[i][0]+a[i][1],i});
            sub.push_back({a[i][0]-a[i][1],i});
        }
        sort(add.begin(),add.end());
        sort(sub.begin(),sub.end());
        return max(add.back()[0]-add[0][0],sub.back()[0]-sub[0][0]);
    }
    int minimumDistance(vector<vector<int>>& a) {
        int n = a.size();
        vector<array<int,2>> add,sub;
        for(int i=0;i<n;i++){
            add.push_back({a[i][0]+a[i][1],i});
            sub.push_back({a[i][0]-a[i][1],i});
        }
        sort(add.begin(),add.end());
        sort(sub.begin(),sub.end());
        int minMaxDis = INT_MAX;
        minMaxDis = min(minMaxDis,cal(a,add.back()[1]));
        minMaxDis = min(minMaxDis,cal(a,add[0][1]));
        minMaxDis = min(minMaxDis,cal(a,sub.back()[1]));
        minMaxDis = min(minMaxDis,cal(a,sub[0][1]));
        return minMaxDis;
    }
};
