#define ll long long
class Solution {
public:
   ll solve(vector<vector<int>>& A){
     int n = A.size();
    vector<int> v(n), vv(n);
    for (int i = 0; i < n; i++) {
        v[i] = A[i][0] + A[i][1];
        vv[i] = A[i][0] - A[i][1];
    }
    sort(v.begin(), v.end());
    sort(vv.begin(), vv.end());

     ll w = 1e9; 
       for(int i=0;i<n;i++){
           ll a = A[i][0] + A[i][1] ; 
           ll b = A[i][0] - A[i][1] ; 
           ll c,d;

           if(v[0]==a){
               c=v[v.size()-1]-v[1];
           }
           else if(v[v.size()-1]==a){
               c=v[v.size()-2]-v[0];
           }
           else{
               c=v[v.size()-1]-v[0];
           }
         //  cout << c << " ";
            if(vv[0]==b){
               d=vv[vv.size()-1]-vv[1];
           }
           else if(vv[vv.size()-1]==b){
               d=vv[vv.size()-2]-vv[0];
           }
           else{
               d=vv[vv.size()-1]-vv[0];
           }
                    //  cout << d << " ";
           ll diff = max(c,d) ; 
           w=min(w,diff);
       }
    return w;
 }

    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        return solve(points);
    }
};
