class Solution {
public:
    int maxMan(vector<vector<int>> points,int idx){
        int n=points.size();
        vector<vector<int>> newP;
        for(int i=0;i<n;i++){
            if(i!=idx) newP.push_back(points[i]);
        }
        points=newP;
        vector<int> add,sub;
        for(auto v:points){
            add.push_back(v[0]+v[1]);
            sub.push_back(v[0]-v[1]);
        }
        sort(add.begin(),add.end());
        sort(sub.begin(),sub.end());
        int mx=max(add.back()-add.front(),sub.back()-sub.front());
        return mx;
    }
    int minimumDistance(vector<vector<int>>& points) {
        vector<vector<int>> add,sub;
        int idx=0;
        for(auto v:points){
            add.push_back({v[0]+v[1],idx});
            sub.push_back({v[0]-v[1],idx});
            idx++;
        }
        sort(add.begin(),add.end());
        sort(sub.begin(),sub.end());
        int res=INT_MAX;
        res=min(res,maxMan(points,add.back()[1]));
        res=min(res,maxMan(points,add.front()[1]));
        res=min(res,maxMan(points,sub.back()[1]));
        res=min(res,maxMan(points,sub.front()[1]));
        return res;
    }
};
