class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        multiset<int> s, ss;
        for(int i = 0; i < points.size(); i++){
            int x = points[i][0], y = points[i][1];
            s.insert(x + y);
            ss.insert(x - y);
        }
        int ans = 1e9;
        for(int i = 0; i < points.size(); i++){
            int x = points[i][0], y = points[i][1];
            s.erase(s.find(x + y));
            ss.erase(ss.find(x - y));
            int m = *s.rbegin() - *s.begin();
            int n = *ss.rbegin() - *ss.begin();
            s.insert(x + y); ss.insert(x - y);
            ans = min(ans, max(m, n));
        }return ans;

    }
};
